 import {dispatchHelper} from '../utils/utils';
 import {GET_USER_POSTS,GET_BOOKMARKED_POSTS,GET_TAGGED_POSTS,GET_SHARED_POSTS,GET_PHOTOS,GET_FEED,GET_GROUP_POSTS,
         GET_GROUP_PHOTOS,GET_POST,GET_POST_LIKES,REMOVE_LIKE,GET_POST_TAGS,GET_USERS_TO_SHARE,GET_POST_SHARERS,ADD_USER_POST,
         ADD_GROUP_POST,EDIT_POST,DELETE_POST,UNTAG,UNBOOKMARK,REMOVE_SHARE,COMMENT,DELETE_COMMENT,EDIT_COMMENT,
         CHANGE_POSTIT_PROPERTIES,RESET_POSTIT} from './types';

 export const getUserPosts = username => dispatchHelper(GET_USER_POSTS,'get-user-posts',{username});
 export const getBookmarkedPosts = user => dispatchHelper(GET_BOOKMARKED_POSTS,'get-bookmarked-posts',{user});
 export const getTaggedPosts = user => dispatchHelper(GET_TAGGED_POSTS,'get-tagged-posts',{user});
 export const getSharedPosts = user => dispatchHelper(GET_SHARED_POSTS,'get-shared-posts',{user});
 export const getPhotos = user => dispatchHelper(GET_PHOTOS,'get-photos',{user});
 export const getFeed = () =>  dispatchHelper(GET_FEED,'get-feed');
 export const getGroupPosts = group => dispatchHelper(GET_GROUP_POSTS,'get-group-posts',{group});
 export const getGroupPhotos = group => dispatchHelper(GET_GROUP_PHOTOS,'get-group-photos',{group});
 export const getPost = post_id => dispatchHelper(GET_POST,'get-post',{post_id});
 export const getPostLikes = post => dispatchHelper(GET_POST_LIKES,'get-post-likes',{post});
 export const removeLike = like_id => dispatchHelper(REMOVE_LIKE,'remove-like',{like_id});
 export const getPostTags = post => dispatchHelper(GET_POST_TAGS,'get-post-tags',{post});
 export const getUsersToShare = post => dispatchHelper(GET_USERS_TO_SHARE,'get-users-to-share',{post});
 export const getPostSharers = post => dispatchHelper(GET_POST_SHARERS,'get-post-sharers',{post});

 export const addUserPost = post => {
     return {
         type: ADD_USER_POST,
         payload: post
     }
 }

 export const addGroupPost = post => {
     return {
         type: ADD_GROUP_POST,
         payload: post
     }
 }

 export const editPost = post_details => {
   return {
       type: EDIT_POST,
       payload: post_details
   }
 }

 export const deletePost = post => {
     return {
         type: DELETE_POST,
         payload: post
     }
 }

 export const untag = user => {
     return {
         type: UNTAG,
         payload: user
     }
 }

 export const unbookmark = post => {
   return {
       type: UNBOOKMARK,
       payload: post
   }
 }

 export const removeShare = share_id => {
     return {
         type: REMOVE_SHARE,
         payload: share_id
     }
 }

 export const comment = comment => {
     return {
         type: COMMENT,
         payload: comment
     }
 }

 export const deleteComment = comment_id => {
     return  {
         type: DELETE_COMMENT,
         payload: comment_id
     }
 }

 export const editComment = comment => {
     return {
         type: EDIT_COMMENT,
         payload: comment
     }
 }

 export const changePostitProperties = (what,values) => {
     return {
         type: CHANGE_POSTIT_PROPERTIES,
         payload: {what,values}
     }
 }

 export const resetPostIt = () => {
     return {
         type: RESET_POSTIT
     }
 }
