 import React,{Component,Fragment} from 'react';
 import {NavLink} from 'react-router-dom';
 import {uData} from '../../../utils/utils';
 import zizu from '../../../assets/images/zizu.jpg';
 import HomeIcon from '@material-ui/icons/Home';
 import TelegramIcon from '@material-ui/icons/Telegram';
 import ExploreIcon from '@material-ui/icons/Explore';
 import FavoriteIcon from '@material-ui/icons/Favorite';
 import SideBar from '../sidebar/sidebar';
import recommendUser from '../../profile/banner/top-options/recommendUser';
import { renderStatic } from 'react-helmet';

 class HeaderTopLinks extends Component {
     state = { showOptions: false}
     toggleOptions = () => {
         this.setState({showOptions: !this.state.showOptions})
     }


    render(){
        const {showOptions} = this.state;
        let id = uData('session');
        let username = uData('username');
        return (
            <Fragment>
                <NavLink to="/notifications" activeClassName="ha_active" className="notification">
                    <span className="notification_span">
                        <HomeIcon />
                        <TelegramIcon />
                        <ExploreIcon />
                        <FavoriteIcon />
                    </span>      
                </NavLink>
    
                <NavLink to={`/profile/${username}`} className="sp" activeClassName="ha_active">
                    <img src={`${zizu}`} alt="avatar">
    
                    </img>
                    <span className="sp_span">{username}</span>
                </NavLink>
            </Fragment>
        )
    }
 }

 export default HeaderTopLinks;
